CREATE TABLE core_settings(
  set_id bigint NOT NULL,
  set_name text,
  set_value text,
  t_created timestamp with time zone,
  t_modified timestamp with time zone,
  t_deleted timestamp with time zone,
  deleted smallint DEFAULT 0 NOT NULL,
  u_createdby bigint,   
  u_modifiedby bigint,
  u_deletedby bigint,
  ip_created text,   
  ip_modified text,
  ip_deleted text
);


COMMENT ON TABLE core_settings IS 'Table settings';
COMMENT ON COLUMN core_settings.set_id IS 'The primary key field of the table';
COMMENT ON COLUMN core_settings.set_name IS 'Name';
COMMENT ON COLUMN core_settings.set_value IS 'Value';
COMMENT ON COLUMN core_settings.t_created IS 'Timestamp at which the row was created';
COMMENT ON COLUMN core_settings.t_modified IS 'Timestamp at which the row was last modified';
COMMENT ON COLUMN core_settings.t_deleted IS 'Timestamp at which the row was soft deleted';
COMMENT ON COLUMN core_settings.deleted IS 'Is the row deleted: 0-No,1=Yes';
COMMENT ON COLUMN core_settings.u_createdby IS 'The user id for those who created this row';
COMMENT ON COLUMN core_settings.u_modifiedby IS 'The user id for those who last modified this row';
COMMENT ON COLUMN core_settings.u_deletedby IS 'The user id for those who deleted this row';
COMMENT ON COLUMN core_settings.ip_created IS 'Created ip address';
COMMENT ON COLUMN core_settings.ip_modified IS 'Last modified ip address';
COMMENT ON COLUMN core_settings.ip_deleted IS 'Deleted ip address';


CREATE SEQUENCE core_settings_set_id_seq
 START WITH 1
    INCREMENT BY 1
    NO MAXVALUE
    NO MINVALUE
    CACHE 1;

ALTER TABLE ONLY core_settings ALTER COLUMN set_id SET DEFAULT nextval('core_settings_set_id_seq');
ALTER TABLE ONLY core_settings ADD CONSTRAINT core_settings_pkey PRIMARY KEY (set_id);



